import React, { useState, useEffect } from 'react';

interface Post {
  title: string;
  link: string;
  pubDate: string;
  contentSnippet: string;
  [key: string]: any;
}

const Blog: React.FC = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const rssUrl = 'https://medium.com/feed/@Ric_Royal';
        const apiUrl = `https://api.rss2json.com/v1/api.json?rss_url=${encodeURIComponent(rssUrl)}`;
        const response = await fetch(apiUrl);
        const data = await response.json();

        if (data.status === 'ok') {
          setPosts(data.items);
        } else {
          console.error('Error fetching blog posts:', data.message);
        }
      } catch (error) {
        console.error('Error fetching blog posts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  if (loading) {
    return (
      <div className="max-w-3xl mx-auto p-4">
        <p>Loading blog posts...</p>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h2 className="text-3xl font-bold mb-4">Blog</h2>
      <p className="mb-4">
        Latest articles and insights related to data analysis:
      </p>
      <ul>
        {posts.map((post, index) => (
          <li key={index} className="mb-6">
            <a href={post.link} target="_blank" rel="noopener noreferrer">
              <h3 className="text-xl font-semibold text-purple-500 hover:underline">
                {post.title}
              </h3>
            </a>
            <p className="text-gray-600">
              {new Date(post.pubDate).toLocaleDateString()}
            </p>
            <div className="mt-2">
              <p>{post.contentSnippet}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Blog;
